import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, FlatList , Image, Button, TextInput} from 'react-native';
import { AsyncStorage } from '@react-native-community/async-storage';

export default function Auth(props) {
    const [username,setUsername] = useState('')
    const [password,setPassword] = useState('')

    const auth = () => {
            fetch(`http://192.168.0.175:8000/auth//`, {
            // fetch('http://127.0.0.1:8000/api/movies/', { 
                method: 'POST',
                headers: {
                    'Authorization' : 'Token 2ea3ee1268d7d28577e1390b71c1ec40545e3ea7',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({username,password})
            }).then(res => res.json())
            .then(res => props.navigation.navigate('MovieList', {movie: movie, title:movie.title}))
            .catch(error => console.log(error))
        
    }      


    return (
        <View style={styles.container}>
            <Text style={styles.label}>Username</Text>
            <TextInput
                style={styles.input}
                placeholder="Username"
                onChangeText={text=>setUsername(text)}
                value={username}
                autoCapitalize={'none'}
            />
            <Text style={styles.label}>Password</Text>
            <TextInput
                style={styles.input}
                placeholder="Password"
                onChangeText={text=>setPassword(text)}
                value={password}
                autoCapitalize={'none'}
                secureTextEntry={true}
            />
            <Button onPress={()=>auth()} title='Login'/>
        </View>
    );
}

Auth.navigationOptions = screenProps => ({
    title: 'Login',
    headerStyle:{
         backgroundColor: 'orange'
    },
    headerTintColor: '#fff',
    headerTitleStyle: {
        fontWeight: 'bold',
        fontSize: 24
    },
    headerRight: () =>  <Button title='Remove' color='orange' onPress={() => removeClicked(screenProps)}/>,
})

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#282C35',
    padding: 10
  },
  
  label: {
      fontSize: 24,
      color: 'white',
      padding: 10
  },

  input : {
    backgroundColor: 'white',
    fontSize: 24,
    padding: 10,
    margin: 10
  },
});
