import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, FlatList , Image, Button, TextInput} from 'react-native';

export default function Edit(props) {
    const movie = props.navigation.getParam('movie', null)
    const [title,setTitle] = useState(movie.title)
    const [description,setDescription] = useState(movie.description)

    const saveMovie = () => {
        props.navigation.goBack()
    }

    useEffect( () => {
        fetch(`http://192.168.0.175:8000/api/movies/${movie.id}/`, {
        // fetch('http://127.0.0.1:8000/api/movies/', { 
            method: 'PUT',
            headers: {
                'Authorization' : 'Token 2ea3ee1268d7d28577e1390b71c1ec40545e3ea7',
            },
            body: {title:title, description:description}
        }).then(res => res.json()).then(res => setMovies(res)).catch(error => console.log(error))
    }, [])

    return (
        <View style={styles.container}>
            <Text style={styles.label}>Title</Text>
            <TextInput
                style={styles.input}
                placeholder="Title"
                onChangeText={text=>setTitle(text)}
                value={title}
            />
            <Text style={styles.label}>Description</Text>
            <TextInput
                style={styles.input}
                placeholder="Description"
                onChangeText={text=>setDescription(text)}
                value={description}
            />
            <Button onPress={()=>saveMovie()} title='Save'/>
        </View>
    );
}

Edit.navigationOptions = screenProps => ({
    title: screenProps.navigation.getParam('movie').title,
    headerStyle: {
        backgroundColor: 'orange'
    },
    headerTintColor: '#fff',
    headerTitleStyle: {
        fontWeight: 'bold',
        fontSize: 24
    },
})
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#282C35',
    padding: 10
  },
  
  label: {
      fontSize: 24,
      color: 'white',
      padding: 10
  },

  input : {
    backgroundColor: 'white',
    fontSize: 24,
    padding: 10,
    margin: 10
  },
});
