import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, FlatList , Image, Button, TextInput} from 'react-native';

export default function Edit(props) {
    const movie = props.navigation.getParam('movie', null)
    const [title,setTitle] = useState(movie.title)

    return (
        <View style={styles.container}>
            <Text style={styles.description}>Edit {movie.title}</Text>
            <TextInput>
                style={styles.input}
                placeholder="Title"
                onChangeText={text=>setTitle(text)}
                value={title}
            </TextInput>
        </View>
    );
}

Edit.navigationOptions = screenProps => ({
    title: screenProps.navigation.getParam('movie').title,
    headerStyle: {
        backgroundColor: 'orange'
    },
    headerTintColor: '#fff',
    headerTitleStyle: {
        fontWeight: 'bold',
        fontSize: 24
    },
})
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#282C35',
    padding: 10
  },

  starContainer : {
      alignItems: 'center',
      justifyContent: 'center',
      flexDirection : 'row'
  },

  orange : {
      color: 'orange'
  },

  white: {
      color: 'white'
  },
  
  description: {
      fontSize: 20,
      color: 'white',
      padding: 10
  },

  input : {

  },
});
